var args = process.argv;
var argsLength = args.length;
if (argsLength < 4) {
    var hash = '\n##################################################\n';
    console.log('\nFor fÃ¥ argumenter:' + hash + 'Riktig bruk er: node concat.js inputdir outputfile' + hash);
}
console.log('Arguments: ', args);

var fs = require('fs');
var files = ['bootstrap-transition.js', 'bootstrap-alert.js', 'bootstrap-button.js', 'bootstrap-carousel.js',
             'bootstrap-collapse.js', 'bootstrap-dropdown.js','bootstrap-modal.js', 'bootstrap-tooltip.js',
             'bootstrap-popover.js', 'bootstrap-scrollspy.js', 'bootstrap-tab.js', 'bootstrap-typeahead.js',
             'bootstrap-affix.js'];
var fullfile = '';

for (var i = 0; i < files.length; i++) {
    var file = files[i];
    fullfile += fs.readFileSync(args[2] + file, 'utf8');
}
fs.appendFileSync(args[3], fullfile, encoding = 'utf8');

