var args = process.argv;
var argsLength = args.length;
if (argsLength < 4) {
    var hash = '\n##################################################\n';
    console.log('\nFor fÃ¥ argumenter:' + hash + 'Riktig bruk er: node copy.js inputdir outputfile' + hash);
}

console.log('Arguments: ', args);

var fs = require('fs');

var files = fs.readdir(args[2], function (err, list) {
    if (err) {
        return err;
    }
    list.forEach(function (file, i) {
        if (fs.statSync(args[2] + file).isFile()) {
            console.log('File: ' + i + ': ', file);
            var fullfile = fs.readFileSync(args[2] + file);
            fs.writeFileSync(args[3] + file, fullfile);
        }
    });
});
