var args = process.argv;
var argsLength = args.length;
if (argsLength < 4) {
    var hash = '\n##################################################\n';
    console.log('\nFor fÃ¥ argumenter:' + hash + 'Riktig bruk er: node copy.js inputdir outputfile' + hash);
}

console.log('Arguments: ', args);

var fs = require('fs');

function copy(fromDir, toDir) {
    var files = fs.readdir(fromDir, function (err, list) {
        if (err) {
            return err;
        }
        list.forEach(function (file, i) {
            if (fs.statSync(fromDir + file).isFile()) {
                console.log('File: ' + i + ': ', file);
                var fullfile = fs.readFileSync(fromDir + file);
                fs.writeFileSync(toDir + file, fullfile);
            }
        });
    });
}
copy(args[2], args[3]);
